angular.module('ngNotify', []).

service('$notify', function(){
	// top-left   || bottom-left
	// top-center || bottom-center
	// top-rigth  || bottom-rigth
	var position = 'toast-bottom-left';

	var time = 5000; // milisegundos
	var timehover = 1000;

	var showCloseButton = true; // Muestra el boton de cerrar notificacion
	var showProgressBar = true; // Muestra la barra bajo la notificacion
	var clickEvent = null; // Al hacer click sobre la notificacion

	// CONFIGURANDO TOASTR
	var configurar = function () {
		toastr.options = {
			'debug': false,
			'preventDuplicates': false,
			'newestOnTop': false,
			'closeButton': showCloseButton,
			'progressBar': showProgressBar,
			'positionClass': position,
			'onclick': clickEvent,
			'timeOut': time,
			'extendedTimeOut': timehover,
			'showDuration': 300,
			'hideDuration': 500,
			'showEasing': "swing",
			'hideEasing': "linear",
			'showMethod': "fadeIn",
			'hideMethod': "fadeOut"
		};
	}
	return{
		success: function (titulo, mensaje) {
			configurar();
			toastr["success"](mensaje, titulo);
		},
		info: function (titulo, mensaje) {
			configurar();
			toastr["info"](mensaje, titulo);
		},
		warning: function (titulo, mensaje) {
			configurar();
			toastr["warning"](mensaje, titulo);
		},
		error: function (titulo, mensaje) {
			configurar();
			toastr["error"](mensaje, titulo);
		},

		setPosition: function (pos) {
			if(!angular.isNumber(pos)){
				position = 'toast-'+pos;
			}
			return this;
		},

		setTime: function (tiempo) {
			if(angular.isNumber(tiempo)){
				time = tiempo*1000;
			}
			return this;
		},
		setTimeExtend: function (tiempo) {
			if(angular.isNumber(tiempo)){
				timehover = tiempo*1000;
			}
			return this;
		},

		showCloseButton: function (show) {
			if(show==false || show==true){
				showCloseButton = show;
			}
			return this;
		},
		showProgressBar: function (show) {
			if(show==false || show==true){
				showProgressBar = show;
			}
			return this;
		},
		onclick: function (func) {
			clickEvent = func;
			return this;
		}
	};
});